import React from 'react';
import { useParams, Link } from 'react-router-dom';
import { projects } from './projectsData';

const ProjectPage = () => {
  const { id } = useParams();
  const project = projects.find(p => p.id === parseInt(id));

  if (!project) {
    return <div>Project not found</div>;
  }

  return (
    <div className="container mx-auto px-4 py-8">
      <Link to="/projects" className="text-blue-500 hover:underline mb-4 inline-block">&larr; Back to Projects</Link>
      <h1 className="text-3xl font-bold mb-4">{project.title}</h1>
      {project.image && <img src={`/images/${project.image}`} alt={project.title} className="w-full h-full object-cover mb-4 rounded" />}
      <div className="flex flex-wrap gap-2 mb-4">
        {project.skills.map((skill, index) => (
          <span key={index} className="bg-gray-200 text-gray-700 px-3 py-1 rounded-full text-sm">
            {skill}
          </span>
        ))}
      </div>
      <p className="text-gray-700 mb-6">{project.fullDescription}</p>
      {/* Add more project details here */}
    </div>
  );
};

export default ProjectPage;
