import React, { useState } from 'react';
import { Home, User, Briefcase, Image, Mail, Menu, X } from 'lucide-react';

const Navigation = ({ activeSection, setActiveSection }) => {
  const [isMenuOpen, setIsMenuOpen] = useState(false);

  const navItems = [
    { name: 'Home', icon: Home },
    { name: 'About', icon: User },
    { name: 'Projects', icon: Briefcase },
    { name: 'Portfolio', icon: Image },
    { name: 'Contact', icon: Mail },
  ];

  return (
    <nav className="bg-gray-800 text-white p-4">
      <div className="container mx-auto flex justify-between items-center">
        <h1 className="text-2xl font-bold">Adam Gordon</h1>
        <div className="hidden md:flex space-x-4">
          {navItems.map((item) => (
            <button
              key={item.name}
              className={`flex items-center space-x-1 ${
                activeSection === item.name ? 'text-orange-500' : 'hover:text-orange-300'
              }`}
              onClick={() => setActiveSection(item.name)}
            >
              <item.icon size={18} />
              <span>{item.name}</span>
            </button>
          ))}
        </div>
        <button
          className="md:hidden"
          onClick={() => setIsMenuOpen(!isMenuOpen)}
        >
          {isMenuOpen ? <X size={24} /> : <Menu size={24} />}
        </button>
      </div>
      {isMenuOpen && (
        <div className="md:hidden mt-4">
          {navItems.map((item) => (
            <button
              key={item.name}
              className={`flex items-center space-x-2 w-full p-2 ${
                activeSection === item.name ? 'bg-gray-700 text-orange-500' : 'hover:bg-gray-700'
              }`}
              onClick={() => {
                setActiveSection(item.name);
                setIsMenuOpen(false);
              }}
            >
              <item.icon size={18} />
              <span>{item.name}</span>
            </button>
          ))}
        </div>
      )}
    </nav>
  );
};

const Section = ({ title, children }) => (
  <section className="py-12">
    <div className="container mx-auto px-4">
      <h2 className="text-3xl font-bold mb-6">{title}</h2>
      {children}
    </div>
  </section>
);

const PortfolioMockup = () => {
  const [activeSection, setActiveSection] = useState('Home');

  const renderSection = () => {
    switch (activeSection) {
      case 'Home':
        return (
          <div className="text-center py-24 bg-gray-100">
            <h1 className="text-5xl font-bold mb-4">Adam Gordon</h1>
            <h2 className="text-2xl mb-8">Engineer | Maker | Content Creator</h2>
            <button className="bg-orange-500 text-white px-6 py-2 rounded-full hover:bg-orange-600 transition duration-300">
              Learn More
            </button>
          </div>
        );
      case 'About':
        return (
          <Section title="About Me">
            <p className="mb-4">
              I'm a biomedical engineer with a passion for innovation and creativity. My professional journey has taken me from NASA to the pharmaceutical industry, where I currently work as an MES Engineer at Emerson, automating pharmaceutical manufacturing processes.
            </p>
            <p className="mb-4">
              Beyond my day job, I'm an avid maker and content creator, exploring various fields such as woodworking, leather crafting, stained glass design, furniture making, and video production.
            </p>
            <button className="bg-gray-800 text-white px-4 py-2 rounded hover:bg-gray-700 transition duration-300">
              View Resume
            </button>
          </Section>
        );
      case 'Projects':
        return (
          <Section title="Featured Projects">
            <div className="grid md:grid-cols-2 gap-6">
              {['Automated Pharmaceutical Process', 'Custom Furniture Design', 'TikTok Content Creation'].map((project) => (
                <div key={project} className="bg-white p-6 rounded-lg shadow-md">
                  <h3 className="text-xl font-semibold mb-2">{project}</h3>
                  <p className="text-gray-600">
                    Lorem ipsum dolor sit amet, consectetur adipiscing elit. Sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.
                  </p>
                </div>
              ))}
            </div>
          </Section>
        );
      case 'Portfolio':
        return (
          <Section title="Portfolio">
            <div className="grid md:grid-cols-3 gap-6">
              {['Woodworking Project', 'Stained Glass Art', 'Medical Device Prototype', 'Leather Goods'].map((item) => (
                <div key={item} className="bg-white p-4 rounded-lg shadow-md">
                  <div className="bg-gray-300 h-48 mb-4 rounded"></div>
                  <h3 className="text-lg font-semibold">{item}</h3>
                </div>
              ))}
            </div>
          </Section>
        );
      case 'Contact':
        return (
          <Section title="Get in Touch">
            <form className="max-w-md mx-auto">
              <input
                type="text"
                placeholder="Name"
                className="w-full mb-4 p-2 border border-gray-300 rounded"
              />
              <input
                type="email"
                placeholder="Email"
                className="w-full mb-4 p-2 border border-gray-300 rounded"
              />
              <textarea
                placeholder="Message"
                rows="4"
                className="w-full mb-4 p-2 border border-gray-300 rounded"
              ></textarea>
              <button
                type="submit"
                className="w-full bg-orange-500 text-white py-2 rounded hover:bg-orange-600 transition duration-300"
              >
                Send Message
              </button>
            </form>
          </Section>
        );
      default:
        return null;
    }
  };

  return (
    <div className="min-h-screen flex flex-col bg-gray-50">
      <Navigation activeSection={activeSection} setActiveSection={setActiveSection} />
      <main className="flex-grow">
        {renderSection()}
      </main>
      <footer className="bg-gray-800 text-white py-6">
        <div className="container mx-auto px-4 text-center">
          <div className="flex justify-center space-x-4 mb-4">
            {['tiktok', 'youtube', 'linkedin', 'github'].map((social) => (
              <a
                key={social}
                href="#"
                className="text-gray-400 hover:text-white transition duration-300"
              >
                <i className={`fab fa-${social} text-2xl`}></i>
              </a>
            ))}
          </div>
          <p>&copy; 2024 Adam Gordon. All rights reserved.</p>
        </div>
      </footer>
    </div>
  );
};

export default PortfolioMockup;
